.amd-product-picker{position:fixed;top:calc(50% - 35vh);width:clamp(300px,60vw,60vw);height:70vh;background:var(--amd-wrapper-bg);border-radius:16px;z-index:999}body.rtl .amd-product-picker{left:calc(calc(50% - clamp(150px,30vw,30vw)) - 80px)}body.ltr .amd-product-picker{right:calc(calc(50% - clamp(150px,30vw,30vw)) - 80px)}@media(max-width:993px){.amd-product-picker{left:calc(50% - 150px) !important;width:300px !important}}.amd-product-picker-overlay{position:fixed;top:0;left:0;width:100%;height:100%;background:rgba(0,0,0,.2);backdrop-filter:blur(5px);-webkit-backdrop-filter:blur(5px);z-index:998}.amd-product-picker>.--title{position:absolute;box-sizing:border-box;justify-content:space-between;top:0;left:0;width:100%;padding:16px}.amd-product-picker>.--content{position:relative;padding:16px;margin-top:66px;overflow:auto;max-height:calc(100% - 100px)}.amd-product-picker-small-thumbnail{display:inline-block;width:60px;border-radius:8px;height:50px;object-fit:contain;background:#fff}.amd-product-picker .adp-admin-table tbody tr>td{min-height:45px}.amd-product-picker .adp-admin-table tbody tr>td,.amd-product-picker .adp-admin-table thead th{text-align:start;padding:8px}