.amd-user-picker.waiting{opacity:1 !important}.amd-user-picker>.--content>.-title,.amd-user-picker{display:flex;align-items:center;justify-content:center}.amd-user-picker{position:fixed;top:0;left:0;width:100%;height:100%;z-index:999}.amd-user-picker>.--overlay{background:rgba(0,0,0,.2);width:100%;height:100%;position:absolute;top:0;left:0}.amd-user-picker>.--content{position:relative;width:clamp(300px,50vw,50vw);min-height:500px;max-height:50vh;background:var(--amd-wrapper-bg);border-radius:16px;overflow-y:auto;overflow-x:auto;-ms-overflow-style:none;scrollbar-width:none}.amd-user-picker>.--content::-webkit-scrollbar{display:none}.amd-user-picker>.--content>.-title{padding:16px;justify-content:space-between}.amd-user-picker>.--content>.-title>*{flex:1}.amd-user-picker>.--content>.-content{box-sizing:border-box;width:100%;padding:16px;margin:auto}